import { useState, useEffect, useMemo } from 'react';

import TablePage  from '../utils/TablePageAdj' 
import axios from "axios";
import { format } from 'date-fns'
import { withRouter, Link } from "react-router-dom";

function AdjustmentPscList() {

	const [data, setData] = useState([]);
	const [update, setUpdate] = useState(false);
	const [serverErrors, setServerErrors] = useState([]);
	const [search, setSearch] = useState("");
	const [inputText, setInputText] = useState('');
	const [action,setAction]=useState('')
	const [disabled,setDisabled]=useState(false);
	const [selectedSection,setSelectedSection]=useState('');
	const [payCodeSearch,setPayCodeSearch]=useState('');
	const [page,setPage]=useState(0);
	const [usrLevel, setUsrLevel] = useState(0);
	const [mesg,setMesg]=useState('');


	useEffect(() => {
		let fetching = false;
		async function fetchData() {

			if(!fetching)
			await axios.get('/adjustments/psc?search='+search+'&payCode='+payCodeSearch)
				.then((response) => {
					 setData(response.data);
					 console.log("--action--:" + response.data['action']);
				})
				.catch((error) => {
					if (error.response)
						setServerErrors(error.response.data.error);
					else
						setServerErrors(error.Error);
				})
		}
		fetchData();
		return () => { fetching = true; }

	}, [update, search,payCodeSearch]);


