/**
 * Code generated using Python
 * @author Raja Reddy
 * Date : 17 Oct 2021
 *
 * Modified By : 
 */

import { useState, useEffect, useMemo } from "react";
import { withRouter, useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from 'axios';
import Table, { SelectColumnFilter } from '../utils/Table'  // 

import * as yup from "yup";

import { yupResolver } from '@hookform/resolvers/yup';
import LiveSearch from '../utils/LiveSearch';

import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import addDays from 'date-fns/addDays'

import Tabs from 'react-bootstrap/Tabs'
import Tab from 'react-bootstrap/Tab'


const schema = yup.object({




});



const CmpGeneration = () => {
	const { register, handleSubmit, setValue, formState: { errors }, clearErrors } = useForm({
		resolver: yupResolver(schema)
	});

	let { id } = useParams();
	//console.log(id);

	let history = useHistory();

 	const [data,setData]=useState([]);
	const [serverErrors, setServerErrors] = useState([]);
	const [entity, setEntity] = useState({});
	const [state, setState] = useState({});

	const [sh3List,setSh3List]=useState([]);

	const [loading, setLoading] = useState(true);
	 const [mesg,setMesg]=useState();

	const [key, setKey] = useState('Page1');
	const [disabled,setDisabled]=useState(false);

	const [lightTheme, setLightTheme] = useState(true);



	useEffect(() => {
		let fetching = false;
		let unmounted = false;

		async function fetchData() {

			if (!fetching)
				//console.log(secId);
				await axios.get(`/schedule3s/cmpGen`)
					.then((response) => {
						console.log("response>>" + response.data);
						//setSh3List(response.data);
						 setData(response.data);;

					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		fetchData();

		return () => { fetching = true; unmounted = true; }
		// eslint-disable-next-line react-hooks/exhaustive-deps
	},[]);



	const onSubmit = (data, event) => {
		event.preventDefault();

		console.log(data);
		console.log("data id--------------"+data.id);

			axios.put("/schedule3s/0/generateCmp",data)
				.then((response) => { 
					console.log("reponse status--------------"+response.status+"--"+response.statusText+"----"+"-h--"+response.headers+"--"+response.data);
					if(response.status===200){
						 setMesg(response.data);
						 setSh3List([]);
						//history.push("/daks/view/"+response.data);
						//history.replace({pathname:'/daks/new/'+response.data});



					}
				})
				.catch((error) => {
					//console.log(error.response.data);
					console.log("response--------"+error.response.status);
					if(error.response.status!==200)
						//history.push("/daks");
					//console.log(error.response.headers);
					if (error.response)
						setServerErrors(error.response.data);
					else
						setServerErrors(error);
				});


		//history.push("/daks");
	}



	const onError = (errors, e) => console.log(errors, e);

	//All foreign keys. 
	// XXXXXX Add search fields and adjust the values as necessary


	const errorCallback = (err) => {
		//console.log(err);
		setServerErrors(err);
	}

	const handleCheckBox = index => (e) =>{

		   console.log(e.target.checked);
			let item = data[index];

		item['select'] = e.target.checked;
		let newData = [...data];
		newData[index] = item;
		setData(newData);
	//	console.log(data);	

	}

	const updateCheckBoxAll = (e) =>{


			for(var k in sh3List){
				sh3List[k].select=e.target.checked;
			}


	}

	const handleInputChange = (e) => {
		console.log(e.target.value);
	//	console.log("handle input change");


	};
	const createCmpFile = () => {

		if(disabled)
			return;
		//console.log(data);

		setDisabled(true);
		let saving = false;
		//console.log(id);
		async function cmpGen() {
			if (!saving)
				axios.put("/schedule3s/0/generateCmp",data)
					.then((response) => {
						  const url = window.open(`${process.env.REACT_APP_BASE_URL}/files/${response.data}`);
						  setData([]);
						  setDisabled(false);
					})
					.catch((error) => {
						//console.log(error);
						//console.log(error.response.status);
						//console.log(error.response.headers);
						if (error.response)
							setServerErrors(error.response.data.error);
						else
							setServerErrors(error.Error);
					})
		}
		cmpGen();

		return () => { saving = true; }
	}

	const columns=useMemo(()=>[


		{

			Header: "select",
		 	accessor : "select",




			Cell: ({ row }) => (
				<div>
					<input type="checkbox" onChange={handleCheckBox(row.index)}  checked={data[row.index]['select']}  />

				</div>
			)
		},
		{
			Header: "Section Code",
			accessor: 'sectionCode',
		},
		{
			Header: "DP Sheet No",
			accessor: 'dpSheetNo',
		},

		{
			Header: "Dp Sheet Date",
			accessor: 'dpSheetDate',
		},
		{
			Header: "Dp Sheet Amt",
			accessor: 'dpSheetAmount',
		},
		{
			Header: "Dps Month",
			accessor: 'dpsMonth',
		},

	],[data])

	return (
		<div className="min-h-screen bg-gray-100 text-gray-900">
			<main className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
				<div className={lightTheme ? 'theme-light' : 'theme-dark'} style={disabled ?{pointerEvents: "none",opacity :"0.4"}:{}}>


					<h1 className="text-xl font-semibold ml-4">CMP File Generation</h1>

					<div className=" ml-2 flex flex-wrap content-start ...">

						<div>
							<button type="submit" onClick={createCmpFile} className=" mt-3  w-40  " >Generate CMP File</button>
						</div>
					</div>


				</div>
				<div className="-mt-2 max-h-1 py-0 ml-0">
					<Table columns={columns} data={data} className="table-auto" />
				</div>
			</main>

		</div>
	);
};


export default withRouter(CmpGeneration);
