				];
				setSectionGroup(record['section'].sectionGroup);
				setSecName(record['section'].sectionName);
				setSecId(record['section'].id);
				fields.forEach(field => setValue(field, record[field]));
				if (!isCancelled) {
					setEntity(record);
					setState(prev => ({ ...prev, state: record }));
				}
			}

			fetchData();
			return () => {
				isCancelled = true;
			};
		}

