import { useState, useEffect, useMemo } from "react";
import { withRouter, useParams } from "react-router-dom";
import axios from 'axios';
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import TableCopy from "../utils/TableCopy";
import Table from "../utils/Table";

const PliList = () => {

    const [data, setData] = useState([]);
    const [search, setSearch] = useState("");

    const [serverErrors, setServerErrors] = useState([]);

    useEffect(() => {
        let fetching = false;
        async function FetchData() {
            await axios.get("/plis?search=" + search)
                .then((response) => {
                    setData(response.data);
                    console.log(response.data);
                })
                .catch((error) => {
                    if (error.response)
                        setServerErrors(error.response.data.error);
                    else
                        setServerErrors(error.Error);
                })
        }
        FetchData();
        return () => { fetching = true; }
    }, [search])

    const columns = useMemo(() => [
        {
            Header: 'Action',
            Cell: ({ row }) => (
                <div>
                    <Link to={"/pliEdit/" + row.original.letterNo}>
                        <button className=" w-16 m-0 p-0 " > Edit </button>
                    </Link>
                </div>
            )
        },
        {
            Header: "Officer Name",
            accessor: 'employee.officerName',
        },

        {
            Header: "Cdao No",
            Cell: ({ row }) => (
                <div>
                    {row.original.employee.cdaoNo}{row.original.employee.checkDigit}
                </div>
            )
        },

    ], [data])

    return (
        <div>
            <div>
                abd
            </div>
            <div className="mt-6 max-h-1">
                <Table columns={columns} data={data} className="table-auto" />
            </div>
        </div>
    )
}

export default PliList;
