import React, { useState ,useEffect} from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom/cjs/react-router-dom';

const DakEdit = () => {
  const [formData, setFormData] = useState({
    multipleEntry: false,
    gemBill: false,
    srcsection: '',
    receivedFrom: '',
    dakYear: '',
    securityGrading: '',
    modeOfReceipt: '',
    regnNo: '',
    referenceNo: '',
    section : '',
    referenceDate: '',
    dakType: '',
    vendor: '',
    subject: '',
    oldDak: '',
    billNo: '',
    billDate: '',
    registrationNo : '',
    billNumber : '',
    amount : ''
  });
  const {id} = useParams()

    useEffect(()=>{
      if(id)
      {
        const getIdData = async () => {
          try {
            const data = await axios.get('http://localhost:8081/daks/'+id)
            setFormData(data.data)
            console.log(data.data)

          } catch (error) {
            console.log(error)
          }
        }

        getIdData()
      }
    })


  // useEffect(() => {
  //   // Function to send form data when formData changes
  //   const sendData = async () => {
  //     try {
  //       // Convert singleMultipleDak to boolean
  //       const formDataToSend = {
  //         ...formData,
  //         singleMultipleDak: formData.singleMultipleDak === 'Single' ? false : true
  //       };

  //       // Adjust the API endpoint URL accordingly
  //       const response = await axios.post('http://localhost:8081/daks', formDataToSend);
  //       console.log('Form data sent:', response.data);
  //       // Handle success response as needed
  //     } catch (error) {
  //       console.error('Error sending form data:', error);
  //       // Handle error as needed
  //     }
  //   };

  //   // Call the sendData function
  //   sendData();
  // }, [formData]); // Trigger the effect whenever formData changes

  const sendData = async () => {
        try {

          // Adjust the API endpoint URL accordingly
          const response = await axios.post('http://localhost:8081/daks',formData );
          console.log('Form data sent:', response.data);
          // Handle success response as needed
        } catch (error) {
          console.error('Error sending form data:', error);
          // Handle error as needed
        }
      };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    const val = type === 'checkbox' ? checked : value;
    setFormData({
      ...formData,
      [name]: val
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    sendData();

    console.log(formData);
  };

  return (
    <div className="container">
      {id ? <h2 className='text-center'>Edit Dak</h2> : <h2 className='text-center'>Add Dak</h2>}
      <form onSubmit={handleSubmit}>
        <div className="row">
          <div className="col-md-6">
            <div className="form-group">
              <label htmlFor="singleMultipleDak">Single/Multiple Dak</label>
              <select
                className="form-control"
                id="multipleEntry"
                name="multipleEntry"
                value={formData.multipleEntry}
                onChange={handleChange}
                required
              >
                <option value="">Select</option>
                <option value={false}>Single</option>
                <option value={true}>Multiple</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="srcsection">Src Section</label>
              <select
                className="form-control"
                id="srcsection"
                name="srcsection"
                value={formData.srcsection}
                onChange={handleChange}
                required
              >
                <option value="">Select</option>
                <option value="admin">Admin</option>
                <option value="records">Records</option>
                <option value="source">Source</option>
                <option value="teller">Teller</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="receivedFrom">Received From</label>
              <select
                className="form-control"
                id="receivedFrom"
                name="receivedFrom"
                value={formData.receivedFrom}
                onChange={handleChange}
                required
              >
                <option value="">Select</option>
                <option value="dadffice">DadOffice</option>
                <option value="others">Others</option>
              </select>
            </div>

            {/* Continue adding fields in the first column */}
          </div>

          <div className="col-md-6">
            <div className="form-group">
              <label htmlFor="gemBill">Gem Bill</label>
              <div className="form-check">
                <input
                  type="checkbox"
                  className="form-check-input"
                  id="gemBill"
                  name="gemBill"
                  checked={formData.gemBill}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="year">Year</label>
              <input
                type="text"
                className="form-control"
                id="year"
                name="year"
                value='2024-2025' disabled
                //onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="securityGrading">Security Grading</label>
              <select
                className="form-control"
                id="securityGrading"
                name="securityGrading"
                value={formData.securityGrading}
                onChange={handleChange}
              >

                <option value="other">Other</option>
                <option value="confidential">Confidential</option>
                <option value="secret">Secret</option>
                <option value="topsecret">TopSecret</option>
                <option value="restricted">Restricted</option>
              </select>
            </div>

            {/* Continue adding fields in the second column */}
          </div>
        </div>

        <div className="row">
          <div className="col-md-6">
            <div className="form-group">
              <label htmlFor="modeOfReceipt">Mode Of Receipt</label>
              <select
                className="form-control"
                id="modeOfReceipt"
                name="modeOfReceipt"
                value={formData.modeOfReceipt}
                onChange={handleChange}
              >
                <option value="">-- Select Type --</option>
                <option value="Regd Letter">Regd Letter</option>
                <option value="Regd Parcel">Regd Parcel</option>
                <option value="Speed Post">Speed Post</option>
                <option value="Ordinary Post">Ordinary Post</option>
                <option value="By Hand">By Hand</option>
                <option value="By Courier">By Courier</option>
                <option value="Fax">Fax</option>
                <option value="Email">Email</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="registrationNo">Registration No</label>
              <input
                type="text"
                className="form-control"
                id="registrationNo"
                name="registrationNo"
                value={formData.registrationNo}
                onChange={handleChange}
              />
            </div>

            {/* Continue adding fields in the first column */}
          </div>

          <div className="col-md-6">
            <div className="form-group">
              <label htmlFor="referenceNo">Reference #</label>
              <input
                type="text"
                className="form-control"
                id="referenceNo"
                name="referenceNo"
                value={formData.referenceNo}
                onChange={handleChange}
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="referenceDate">Reference Date</label>
              <input
                type="date"
                className="form-control"
                id="referenceDate"
                name="referenceDate"
                value={formData.referenceDate}
                onChange={handleChange}
                required
              />
            </div>

            {/* Continue adding fields in the second column */}
          </div>
        </div>
        <div className="row">
          <div className="col-md-6">
            {/* Previous fields */}
            <div className="form-group">
              <label htmlFor="section">Section</label>
              <select
                className="form-control"
                id="section"
                name="section"
                value={formData.section}
                onChange={handleChange}
                required
              >
                <option value="">Select</option>
                <option value="Section 1">Section 1</option>
                <option value="Section 2">Section 2</option>
                <option value="Section 3">Section 3</option>
                <option value="Section 4">Section 4</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="dakType">Dak Type</label>
              <select
                className="form-control"
                id="dakType"
                name="dakType"
                value={formData.dakType}
                onChange={handleChange}
                required
              >
                <option value="">Select</option>
                <option value="Type 1">Type 1</option>
                <option value="Type 2">Type 2</option>
                <option value="Type 3">Type 3</option>
                <option value="Type 4">Type 4</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="billDate">Bill Date (ddMMyyyy) :*</label>
              <input
                type="date"
                className="form-control"
                id="billDate"
                name="billDate"
                value={formData.billDate}
                onChange={handleChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="amount">Amount :*</label>
              <input
                type='number'
                className="form-control"
                id="amount"
                name="amount"
                value={formData.amount}
                onChange={handleChange}
                required
              />
            </div>

            {/* Add more fields here */}
          </div>

          <div className="col-md-6">
            {/* Previous fields */}
            <div className="form-group">
              <label htmlFor="vendor">Vendor</label>
              <select
                className="form-control"
                id="vendor"
                name="vendor"
                value={formData.vendor}
                onChange={handleChange}
              >
                <option value="">Select</option>
                <option value="Vendor 1">Vendor 1</option>
                <option value="Vendor 2">Vendor 2</option>
                <option value="Vendor 3">Vendor 3</option>
                <option value="Vendor 4">Vendor 4</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="subject">Subject</label>
              <input
                type="text"
                className="form-control"
                id="subject"
                name="subject"
                value={formData.subject}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="oldDak">Old Dak</label>
              <input
              type='text'
                className="form-control"
                id="oldDak"
                name="oldDak"
                value={formData.oldDak}
                onChange={handleChange}
              ></input>
            </div>

            <div className="form-group">
              <label htmlFor="billNumber">Bill #*</label>
              <input
                type="text"
                className="form-control"
                id="billNumber"
                name="billNumber"
                value={formData.billNumber}
                onChange={handleChange}
                required
              />
            </div>



            {/* Add more fields here */}
          </div>
        </div>

        {/* Add more rows for additional fields */}

        <button type="submit" className="btn btn-primary mt-3" >Submit</button>
      </form>
    </div>
  );
  }
export default DakEdit;
